// Supondo que a entrada venha como items[0].json.output
const data = items[0].json.output;

const doc = {
  titulo: `Proposta – ${data.nome}`,
  data_recebimento: new Date(data.data_de_recebimento).toLocaleDateString("pt-BR"),
  cliente: {
    nome: data.nome,
    email: data.email,
    motivo: data.motivo_do_contato,
  },
  projeto: {
    resumo: data.resumo,
    escopo: data.escopo,
    escopo_resumido: data.escopo_resumido,
    estimativa: data.estimativa_de_esforco,
    estimativa_resumida: data.estimativa_resumida,
    stack_sugerida: data.stack_sugerida,
    riscos: data.riscos_resumidos,
  },
  reuniao: {
    perguntas: data.perguntas_para_reuniao.split("?").map(p => p.trim()).filter(Boolean)
  },
  comunicacao: {
    assunto_email: data.subject,
    mensagem_email: data.mensagem_email
  }
};

return [{ json: doc }];

